# Counts and compares the # substrings in given string beginning with vowels vs. consonants (Minion Game)

from itertools import combinations

VOWELS = "AEIOU"

def countSubstr(string):
    scoreC = scoreV = 0
    for indx in range(len(string)):
        if string[indx] in VOWELS:
            scoreV += len(string) - indx
        else:
            scoreC += len(string) - indx
    return max(scoreC, scoreV)

def countSubstrComb(string):
    scoreC = scoreV = 0
    #for indx1, indx2 in filter(lambda x: x[1] > x[0], combinations(range(len(string)+1), 2)): # XXX SLOWER XXX
    for indx1, indx2 in combinations(range(len(string)+1), 2):
        if indx2 < indx1:
            continue
        if string[indx1] in VOWELS:
            scoreV += 1
        else:
            scoreC += 1
    return max(scoreC, scoreV)

def countSubstrCounts(string):
    scoreC = scoreV = 0
    for indx1 in range(len(string)):
        for indx2 in range(indx1+1, len(string)+1):
            if string[indx1] in VOWELS:
                scoreV += 1
            else:
                scoreC += 1
    return max(scoreC, scoreV)

def countSubstrDict(string):
    cSubStr = {}
    vSubStr = {}
    for indx1 in range(len(string)):
        targetDict = vSubStr if string[indx1] in VOWELS else cSubStr
        for indx2 in range(indx1+1, len(string)+1):
            if string[indx1:indx2] not in targetDict:
                targetDict[string[indx1:indx2]] = 0
            targetDict[string[indx1:indx2]] += 1
    scoreC = sum(cSubStr.values())
    scoreV = sum(vSubStr.values())
    return max(scoreC, scoreV)

tests = [["BANANA", 12]]
tests += [["", 40454713]]
for test in tests:
    print(test[1], countSubstr(test[0]))